import React, { useState } from 'react'

export const AddEvent = () => {
    const [title,setTitle]=useState('');
    const [description,setDescription]=useState('');
    const [location,setLocation]=useState('');
    const [maxAttendees,setMaxAttendees]=useState('');
    const [registrationFees,setRegistrationFees]=useState('');
    const saveEvent = (e) => {e.preventDefault();
        const event={title,description,location,maxAttendees,registrationFees}

        console.log("Event feed from home:", event);
        EventService.addList
    }
  return (
    <div>
        <div className="container">
            <div className="card col-md-6 offset-md-3">
                <h2 className="text-center">Add event</h2>
                <div className="card-body">
                    <form>
                        {/* Title of the event text box */}
                        <div className="form-group mb-2">
                            <label className="form-label">Title of the event</label>
                            <input type="text" placeholder='Enter the title of the event' name="titleEvent"
                            value={title} className='form-control' 
                            onChange={(e)=>{setTitle(e.target.value)}} />
                        </div>
                        {/* Description of the event text box */}
                        <div className="form-group mb-2">
                            <label className="form-label">Requirement of the event</label>
                            <input type="text" placeholder='Enter the description of the event' name="descriptionEvent"
                            value={description} className='form-control' 
                            onChange={(e)=>{setDescription(e.target.value)}} />
                        </div>
                        {/* Location of the event text box */}
                        <div className="form-group mb-2">
                            <label className="form-label">Location of the event</label>
                            <input type="text" placeholder='Enter the location of the event' name="titleEvent"
                            value={location} className='form-control' 
                            onChange={(e)=>{setLocation(e.target.value)}} />
                        </div>
                        {/* max attendees of the event text box */}
                        <div className="form-group mb-2">
                            <label className="form-label">Max attendees of the event</label>
                            <input type="text" placeholder='Enter the Max number of attendees it can accomodate in the event' name="titleEvent"
                            value={maxAttendees} className='form-control' 
                            onChange={(e)=>{setMaxAttendees(e.target.value)}} />
                        </div>
                        {/* Registration fees of the event text box */}
                        <div className="form-group mb-2">
                            <label className="form-label">Registration fees of the event</label>
                            <input type="text" placeholder='Enter the registration fees of the event' name="titleEvent"
                            value={registrationFees} className='form-control' 
                            onChange={(e)=>{setRegistrationFees(e.target.value)}} />
                        </div>
                        {/* submit-button  */}
                        <button onClick={(e)=>saveEvent(e)} className='btn btn-success'>Save Button</button>
                    </form>
                </div>
            </div>

        </div>
    </div>
  )
}
